INSERT INTO `fw_OLD_einsatz_SAVE2` (`id`, `nr`, `datum`, `zeit`, `zeit2`, `titel`, `anzahl`, `fahrzeuge`, `ausgang`, `bericht`, `weitere`, `art`) VALUES
(550, 61, '2006-04-07', '11:53:00', '13:50:00', 'Person in Erdfräse eingeklemmt', '10', 'RW, LF 16/2, KdoW', 'Person in Erdfräse masiv eingeklemmt. Unter- und Oberschenkel mit offenen Wunden und Brüchen. Ein Beim komplett in der Fräse eingeklemmt.', '<b>Person durch Erdfräse schwer verletzt</b>\r\nDie Feuerwehren Bad Soden und Altenhain wurden am Freitag Mittag zu einem schwerem Gartenunfall alarmiert. Eine Person wurde bei Arbeiten mit einer Erdfräse im Gerät eingeklemmt und schwer am Bein verletzt.<br>\r\nAls die ersten Einsatzkräfte der Feuerwehr eintrafen wurde die Person bereits von Rettungsdienst und Notarzt versorgt. Nach Rücksprache mit dem anwesenden Notarzt wurden Teile der Fräse abgetrennt um die Person befreien zu können. Der intubiert beatmete Patient wurde dann im weiteren Verlauf auf einer Vakuummatratze zum nachgeforderten Rettungshubschrauber transportiert. Dieser flog ihn in eine Frankfurter Klinik.<br>\r\nZwei weitere Patienten wurden von der Feuerwehr erstversorgt bis ein zweiter Rettungswagen an der Einsatzstelle eintraf. (rj)', 'Feuerwehr Altenhain, Rettungsdienst (2 RTW), Notarzt (1 NEF, 1 RTH)', '1'),
(551, 62, '2006-04-09', '20:59:00', '21:50:00', 'Auslösung der Brandmeldeanlage', '22', 'DLK 23/12, TLF 24/50, LF 16/1, ELW 1, Wachbesatzung', 'Fehlauslösung der Brandmeldeanlage.', 'Kein Einsatz für die Feuerwehr.', 'Feuerwehren Sulzbach und Schwalbach, Rettungsdienst (1 RTW), DRK OV Liederbach (1 RTW)', '5,2'),
(552, 63, '2006-04-11', '21:15:00', '21:45:00', 'Auslösung der Brandmeldeanlage', '15', 'ELW 1, LF 16/1, Wachbesatzung', 'Böswillige Auslösung der Brandmeldeanlage durch eingeschlagenen Druckknopfmelder.', 'Kein Einsatz für die Feuerwehr.', 'Feuerwehren Neuenhain und Schwalbach, Rettungsdienst (1 RTW), Polizei', '5'),
